    let filteredExpenses = expenses.filter((exp) => {
      return exp.date.getFullYear().toString() === selectedYear;
    });

    console.log(filteredExpenses);
  }
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
